import React, { useEffect, useState } from "react";
import "./UserRegistration";

interface Errors {
  userName?: string;
  postalCode?: string;
  address?: string;
  email?: string;
}

interface User {
  id: number;
  userName: string;
  postalCode?: string;
  address: string;
  email?: string;
  isEditing?: boolean;
}



const UserRegistration = () => {
  const [newUserName, setNewUserName] = useState("");
  const [newAddress, setNewAddress] = useState("");
  const [newUserEmail, setNewUserEmail] = useState("");
  const [users, setUsers] = useState<User[]>([]);


  const handleAddUser = () => {
    const nextId = users.length === 0 ? 1 : Math.max(...users.map(u => u.id)) + 1;

    const newUser: User = {
      id: nextId,
      userName: newUserName,
      address: newAddress,
      email: newUserEmail,
      // 他のプロパティもここに含める必要があります。
    };

    // ユーザーリストを更新する
    setUsers([...users, newUser]);

    // 入力フィールドをクリアする
    setNewUserName("");
    setNewAddress("");
    setNewUserEmail("");
  };

  return (
    <div>
      <div className="user-form">
        <input
          type="text"
          value={newUserName}
          onChange={(e) => setNewUserName(e.target.value)}
          placeholder="ユーザー名"
        />
        <input
          type="text"
          value={newUserEmail}
          onChange={(e) => setNewUserEmail(e.target.value)}
          placeholder="ユーザーアドレス"
        />
        <input
          type="text"
          value={newAddress}
          onChange={(e) => setNewAddress(e.target.value)}
          placeholder="居住地"
        />
        <button onClick={handleAddUser}>登録</button>
      </div>

      <div className="table-container">
        {users.length > 0 && (
          <table className="table">
            <thead>
              <tr>
                <th>ユーザー名</th>
                <th>ユーザーアドレス</th>
                <th>居住地</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user, index) => (
                <tr key={index}>
                  <td>{user.userName}</td>
                  <td>{user.email}</td>
                  <td>{user.address}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default UserRegistration;
