import React, { useEffect, useState } from "react";
import "./UserRegistration";

interface Errors {
  userName?: string;
  postalCode?: string;
  address?: string;
}

interface User {
  id: number;
  userName: string;
  postalCode?: string;  // Make postalCode optional
  address: string;
  isEditing?: boolean;  // Make isEditing optional
}


const UserRegistration = () => {
  const [newUserName, setNewUserName] = useState<string>("");
  const [newAddress, setNewAddress] = useState<string>("");
  const [users, setUsers] = useState<User[]>([]);
  const [editingId, setEditingId] = useState<number | null>(null);
  const [errors, setErrors] = useState<Errors>({});

  // ローカルストレージからデータを読み込む
  useEffect(() => {
    const storedUsers = localStorage.getItem("users");
    if (storedUsers) {
      setUsers(JSON.parse(storedUsers));
    }
  }, []);

  // ユーザーリストが変更された時にローカルストレージを更新する
  useEffect(() => {
    localStorage.setItem("users", JSON.stringify(users));
  }, [users]);

  const handleRegister = () => {
    const newUser: User = {
      id: Date.now(),
      userName: newUserName,
      address: newAddress,
    };
    setUsers([...users, newUser]);
    setNewUserName('');
    setNewAddress('');
    setErrors({}); 
  };

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault(); // Prevent the default form submit behavior
    if (validateForm()) {
      handleRegister();
    }
  };

  const handleEdit = (id: number) => {
    const user = users.find((user) => user.id === id);
    if (user) {
      setEditingId(id);
      setNewUserName(user.userName);
      setNewAddress(user.address);
    }
  };

  const handleSave = (id: number) => {
    setUsers(
      users.map((user) =>
        user.id === id
          ? { ...user, userName: newUserName, address: newAddress }
          : user
      )
    );
    setEditingId(null);
    setNewUserName("");
    setNewAddress("");
  };

  const validateForm = () => {
    let formIsValid = true;
    let tempErrors: Errors = {};

    if (!newUserName.trim()) {
      formIsValid = false;
      tempErrors.userName = "ユーザー名を入力してください。";
    }
    if (!newAddress.trim()) {
      formIsValid = false;
      tempErrors["address"] = "住所を入力してください。";
    }

    setErrors(tempErrors); // Update the errors state
    return formIsValid;
  };

    return (
    <div>
      <form onSubmit={handleSubmit}>
        <table>
          <thead>
            <tr>
              <th>ユーザー名</th>
              <th>ユーザーアドレス</th>
              <th>居住地</th>
            </tr>
          </thead>
          <tbody>
            {/* The existing users are not displayed anymore */}
            <tr>
              <td>
                <input
                  type="text"
                  value={newUserName}
                  onChange={(e) => setNewUserName(e.target.value)}
                  placeholder="ユーザー名"
                />
                {errors.userName && <div className="error">{errors.userName}</div>}
              </td>
              <td>
                {/* Placeholder input for user's email address */}
                <input
                  type="email"
                  placeholder="ユーザーアドレス"
                  // You will need to add state handling for this new field
                />
                {/* You will need to add error handling for this new field */}
              </td>
              <td>
                <input
                  type="text"
                  value={newAddress}
                  onChange={(e) => setNewAddress(e.target.value)}
                  placeholder="居住地"
                />
                {errors.address && <div className="error">{errors.address}</div>}
              </td>
            </tr>
          </tbody>
        </table>
        <div>
          {/* Buttons for submitting the form and for initiating an edit */}
          <button type="submit">登録</button>
          {/* The edit button can be implemented if needed */}
        </div>
      </form>
    </div>
  );
};

export default UserRegistration;
