import React, { useEffect } from 'react';
import useGoogleMapsAPI from './useGoogleMapsAPI';

const App: React.FC = () => {
  // const isAPILoaded = useGoogleMapsAPI('YOUR_API_KEY');
  const isAPILoaded = useGoogleMapsAPI('AIzaSyD6wX1EXIdF3LdenSirmFCWipecY_D_1rg');

  useEffect(() => {
    if (isAPILoaded && window.google && window.google.maps) {
      new window.google.maps.Map(document.getElementById("map") as HTMLElement, {
        center: { lat: -34.397, lng: 150.644 },
        zoom: 8,
      });
    }
  }, [isAPILoaded]);

  return <div id="map" style={{ width: '100vw', height: '100vh' }}></div>;
};

export default App;
