// Navbar.tsx
import React, { useState } from 'react';
// import { Link } from 'react-router-dom';
import { BrowserRouter as Router, Link } from 'react-router-dom';

const Navbar = () => {
  const [isAccordionOpen, setIsAccordionOpen] = useState(false);
  const [isAdmin, setIsAdmin] = useState(true); // 仮の管理者状態フラグ

  // アコーディオンを切り替える関数
  const toggleAccordion = () => {
    setIsAccordionOpen(!isAccordionOpen);
  };

    return (
      <nav className="navbar">
        <h1 className="navbar-logo">My Application</h1>
        <div className="navbar-container">
          {/* ハンバーガーメニューボタン */}
          <button className="hamburger" onClick={toggleAccordion}>
            {/* ハンバーガーライン... */}
          </button>
          {/* アコーディオンの内容 */}
          {isAccordionOpen && (
            <div className="accordion-content">
              {/* トップ画面へのリンク */}
              <Link className="menu-item" to="/">Home</Link>
              {/* 管理者用メニュー */}
              {isAdmin && (
                <>
                  <Link className="menu-item" to="/admin/button1">Admin Button 1</Link>
                  <Link className="menu-item" to="/admin/button2">Admin Button 2</Link>
                  {/* その他の管理者用メニュー項目... */}
                </>
              )}
              {/* ユーザー用メニュー */}
              <Link className="menu-item" to="/button3">User Button 3</Link>
              <Link className="menu-item" to="/button4">User Button 4</Link>
              {/* その他のユーザー用メニュー項目... */}
            </div>
          )}
        </div>
      </nav>
    );
  };

export default Navbar;
