import React, { useEffect, useState } from "react";
import "./UserRegistration.css";

interface Errors {
  userName?: string;
  postalCode?: string;
  address?: string;
  email?: string;
}

interface User {
  id: number;
  userName: string;
  postalCode?: string;
  address: string;
  email?: string;
  isEditing?: boolean;
}

const UserRegistration = () => {
  const [editUserId, setEditUserId] = useState<number | null>(null);
  const [editUserName, setEditUserName] = useState<string>("");
  const [editAddress, setEditAddress] = useState<string>("");
  const [editUserEmail, setEditUserEmail] = useState<string>("");


  const addUser = () => {
    if (validate()) {
      const newUser = {
        id: Math.max(0, ...users.map(u => u.id)) + 1, // 一意のIDを生成
        userName: newUserName,
        address: newAddress,
        email: newUserEmail,
        isEditing: false,
      };
      setUsers([...users, newUser]);

      // フォームをクリアしたくない場合は、以下の行をコメントアウトします
      // setNewUserName('');
      // setNewAddress('');
      // setNewUserEmail('');
    }
  };

  const editUser = (user: User) => {
    setEditUserId(user.id);
    setEditUserName(user.userName);
    setEditAddress(user.address);
    setEditUserEmail(user.email);
  };




  const saveEdit = () => {
    const updatedUsers = users.map(user => {
      if (user.id === editUserId) {
          return {
            ...user,
            userName: editUserName,
            address: editAddress,
            email: editUserEmail  
          };
        }
        return user;
      });
      setUsers(updatedUsers);
      setEditUserId(null);
      // ...フォームクリアのコード...
    };

  return (
    <div>
      <div className="table-container">
        <table className="table">
          <tbody>
            <tr>
              <th>ユーザー名</th>
              <td>
                <input
                  type="text"
                  className="input-no-border"
                  value={newUserName}
                  onChange={(e) => setNewUserName(e.target.value)}
                  placeholder="ユーザー名"
                />
                {errors.userName && (
                  <div className="error">{errors.userName}</div>
                )}
              </td>
            </tr>
            <tr>
              <th>ユーザーアドレス</th>
              <td>
                <input
                  type="text"
                  className="input-no-border"
                  value={newUserEmail}
                  onChange={(e) => setNewUserEmail(e.target.value)}
                  placeholder="ユーザーアドレス"
                />
                {errors.email && <div className="error">{errors.email}</div>}
              </td>
            </tr>
            <tr>
              <th>居住地</th>
              <td>
                <input
                  type="text"
                  className="input-no-border"
                  value={newAddress}
                  onChange={(e) => setNewAddress(e.target.value)}
                  placeholder="居住地"
                />

                {errors.address && (
                  <div className="error">{errors.address}</div>
                )}
              </td>
            </tr>
          </tbody>
        </table>
      </div>

      <div className="button-container">
        <button onClick={addUser}>登録</button> {/* ここを追加 */}
        {users.map((user) =>
          user.isEditing ? (
            <button onClick={() => saveEdit(user.id)}>保存</button>
          ) : (
            <button onClick={() => toggleEdit(user.id)}>編集</button>
          )
        )}
      </div>

    </div>
  );
};

export default UserRegistration;
