import React, { useEffect, useState } from 'react';
import GoogleMapDisplay from './GoogleMapDisplay';

type AddressInfo = {
  address: string;
  latitude?: number;
  longitude?: number;
  order: number;
};

const AdminPage = () => {

    const [addresses, setAddresses] = useState<string[]>(
      JSON.parse(localStorage.getItem('addresses') || '[]')
    );
    const [currentAddress, setCurrentAddress] = useState('');

    useEffect(() => {
      // アドレスリストが更新されたらローカルストレージに保存する
      localStorage.setItem('addresses', JSON.stringify(addresses));
    }, [addresses]);


    const addAddress = () => {
      if (currentAddress.trim() !== '' && currentOrder.trim() !== '') {
        const order = parseInt(currentOrder);
        if (!isNaN(order)) {
          // 既存の順番を更新
          const updatedAddresses = addresses.map((addr) => ({
            ...addr,
            order: addr.order >= order ? addr.order + 1 : addr.order
          }));

          // 新しいアドレス情報を追加
          updatedAddresses.push({
            address: currentAddress,
            // 緯度と経度はAPIから取得
            order,
          });

          // 順番に従ってソート
          updatedAddresses.sort((a, b) => a.order - b.order);

          setAddresses(updatedAddresses);
          setCurrentAddress('');
          setCurrentOrder('');

          // ローカルストレージに保存
          localStorage.setItem('addresses', JSON.stringify(updatedAddresses));
        }
      }
    };

        // 住所リストを表形式で表示する関数
        const renderAddressTable = () => {
          return (
            <table>
              <thead>
                <tr>
                  <th>登録住所</th>
                  <th>緯度</th>
                  <th>経度</th>
                </tr>
              </thead>
              <tbody>
                {addresses.map((address, index) => (
                  <tr key={index}>
                    <td>{address}</td>
                    <td>{/* 緯度データ */}</td>
                    <td>{/* 経度データ */}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          );
        };


  return (
    <div>
      <input
        type="text"
        value={currentAddress}
        onChange={(e) => setCurrentOrder(e.target.value)}
        placeholder="順番を入力"
      />
      <button onClick={addAddress}>住所を追加</button>
      {renderAddressTable()} {/* この行を追加 */}
      <GoogleMapDisplay addresses={addresses} />
    </div>
  );
};

export default AdminPage;
