import React, { useEffect, useState } from "react";

interface User {
  id: number;
  userName: string;
  address: string;
  email: string;
}

const UserRegistration = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [editUserId, setEditUserId] = useState<number | null>(null);
  const [draftUser, setDraftUser] = useState<User | null>(null);

  useEffect(() => {
    const storedUsers = localStorage.getItem("users");
    if (storedUsers) {
      setUsers(JSON.parse(storedUsers));
    }
  }, []);

  const handleEditClick = (user: User) => {
    setEditUserId(user.id);
    setDraftUser({ ...user });
  };

  const handleSave = (id: number) => {
    if (draftUser) {
      const updatedUsers = users.map((user) => {
        if (user.id === id) {
          return { ...draftUser };
        }
        return user;
      });
      setUsers(updatedUsers);
      localStorage.setItem("users", JSON.stringify(updatedUsers));
    }
    setEditUserId(null);
    setDraftUser(null);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>, field: keyof User) => {
    if (draftUser) {
      setDraftUser({ ...draftUser, [field]: e.target.value });
    }
  };

  return (
    <div className="table-container">
      <table className="table">
        <thead>
          <tr>
            <th>ユーザー名</th>
            <th>アドレス</th>
            <th>メール</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>
                {editUserId === user.id ? (
                  <input
                    type="text"
                    value={draftUser?.userName || ""}
                    onChange={(e) => handleChange(e, "userName")}
                  />
                ) : (
                  user.userName
                )}
              </td>
              <td>
                {editUserId === user.id ? (
                  <input
                    type="text"
                    value={draftUser?.address || ""}
                    onChange={(e) => handleChange(e, "address")}
                  />
                ) : (
                  user.address
                )}
              </td>
              <td>
                {editUserId === user.id ? (
                  <input
                    type="text"
                    value={draftUser?.email || ""}
                    onChange={(e) => handleChange(e, "email")}
                  />
                ) : (
                  user.email
                )}
              </td>
              <td>
                {editUserId === user.id ? (
                  <button onClick={() => handleSave(user.id)}>保存</button>
                ) : (
                  <button onClick={() => handleEditClick(user)}>編集</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserRegistration;
