import React, { useEffect, useState } from "react";
import "./UserRegistration.css";

interface Errors {
  userName?: string;
  postalCode?: string;
  address?: string;
  email?: string;
}

interface User {
  id: number;
  userName: string;
  postalCode?: string;
  address: string;
  email?: string;
  isEditing?: boolean;
}

const UserRegistration = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [errors, setErrors] = useState<Errors>({});

  // 新しいユーザー用のステート
  const [newUserName, setNewUserName] = useState<string>("");
  const [newAddress, setNewAddress] = useState<string>("");
  const [newUserEmail, setNewUserEmail] = useState<string>("");

  // 編集用のステート
  const [editUserId, setEditUserId] = useState<number | null>(null);
  const [editUserName, setEditUserName] = useState<string>("");
  const [editAddress, setEditAddress] = useState<string>("");
  const [editUserEmail, setEditUserEmail] = useState<string>("");

  useEffect(() => {
    const storedUsers = localStorage.getItem("users");
    if (storedUsers) {
      setUsers(JSON.parse(storedUsers));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("users", JSON.stringify(users));
  }, [users]);

  const validate = () => {
    let tempErrors: Errors = {};
    if (!newUserName) tempErrors.userName = "ユーザー名を入力してください。";
    if (!newUserEmail) tempErrors.email = "メールアドレスを入力してください。";
    if (!newAddress) tempErrors.address = "住所を入力してください。";
    setErrors(tempErrors);
    return Object.keys(tempErrors).length === 0;
  };

  const addUser = () => {
    if (validate()) {
      const newUser = {
        id: Math.max(0, ...users.map(u => u.id)) + 1, // 一意のIDを生成
        userName: newUserName,
        address: newAddress,
        email: newUserEmail,
      };
      setUsers([...users, newUser]);

      setUsers([...users, newUser]);
      setNewUserName('');
      setNewAddress('');
      setNewUserEmail('');
    }
  };

  const editUser = (user: User) => {
    setEditUserId(user.id);
    setEditUserName(user.userName);
    setEditAddress(user.address);
    setEditUserEmail(user.email ?? "");
  };




  const saveEdit = () => {
    if (editUserId === null) return;
    const updatedUsers = users.map(user => {
      if (user.id === editUserId) {
        return {
          ...user,
          userName: editUserName,
          address: editAddress,
          email: editUserEmail,
        };
      }
      return user;
    });
    setUsers(updatedUsers);
    setEditUserId(null);
    setEditUserName('');
    setEditAddress('');
    setEditUserEmail('');
  };

  return (
    <div>
      <div className="table-container">
        <table className="table">
          <tbody>
            <tr>
              <th>ユーザー名</th>
              <td>
                <input
                  type="text"
                  className="input-no-border"
                  value={newUserName}
                  onChange={(e) => setNewUserName(e.target.value)}
                  placeholder="ユーザー名"
                />
                {errors.userName && (
                  <div className="error">{errors.userName}</div>
                )}
              </td>
            </tr>
            <tr>
              <th>ユーザーアドレス</th>
              <td>
                <input
                  type="text"
                  className="input-no-border"
                  value={newUserEmail}
                  onChange={(e) => setNewUserEmail(e.target.value)}
                  placeholder="ユーザーアドレス"
                />
                {errors.email && <div className="error">{errors.email}</div>}
              </td>
            </tr>
            <tr>
              <th>居住地</th>
              <td>
                <input
                  type="text"
                  className="input-no-border"
                  value={newAddress}
                  onChange={(e) => setNewAddress(e.target.value)}
                  placeholder="居住地"
                />

                {errors.address && (
                  <div className="error">{errors.address}</div>
                )}
              </td>
            </tr>
          </tbody>
        </table>
      </div>

      <div className="button-container">
        <button onClick={addUser}>登録</button>
      </div>

      {/* ユーザーリストと編集ボタン */}
      {users.map((user) => (
        <div key={user.id}>
          {user.userName} - {user.address} - {user.email}
          <button onClick={() => editUser(user)}>編集</button>
        </div>
      ))}

      {/* 編集用フォーム */}
      {editUserId !== null && (
        <div>
          <input
            type="text"
            value={editUserName}
            onChange={(e) => setEditUserName(e.target.value)}
            placeholder="ユーザー名"
          />
          <input
            type="text"
            value={editAddress}
            onChange={(e) => setEditAddress(e.target.value)}
            placeholder="住所"
          />
          <input
            type="text"
            value={editUserEmail}
            onChange={(e) => setEditUserEmail(e.target.value)}
            placeholder="メールアドレス"
          />
          <button onClick={saveEdit}>保存</button>
        </div>
      )}

      {/* 既存のユーザーリスト表示 */}
      <div className="users-list">
        {users.map(user => (
          <div key={user.id} className="user-item">
            <span>{user.userName} - {user.address} - {user.email}</span>
            <button onClick={() => editUser(user)}>編集</button>
          </div>
        ))}
      </div>

      </div>

  );
};

export default UserRegistration;
